body.script:
//
var hT;
const ENGINE_AUTHOR = "jDarkxng";

function triggerHud(){
  var g=document.getElementById('jdark-hud-gate');
  if(!g)return;
  g.classList.add('active');
  clearTimeout(hT);
  hT=setTimeout(function(){g.classList.remove('active');},4000);
}

function exitEngine(){
  if(document.fullscreenElement){document.exitFullscreen();}
  window.location.hash="";
}

function toggleFullscreen(){
  if(!document.fullscreenElement){
    document.documentElement.requestFullscreen().catch(e => console.log(e));
  } else {
    if(document.exitFullscreen){document.exitFullscreen();}
  }
}

// Controlador del Launcher Engine v1.0
(function(owner){
  var APPS={
    // URLs limpias para el reacomodo bajo licencia CC BY-NC-ND 4.0
    "triada":"about:blank",
    "core-flux":"about:blank"
  };

  function runner(){
    var eng=document.getElementById('launcher-engine'),
        frm=document.getElementById('engine-frame'),
        ld=document.getElementById('engine-loading'),
        hud=document.getElementById('jdark-hud-gate'),
        h=window.location.hash.toLowerCase(),
        u=window.location.href.toLowerCase();

    if(h==='#play'||h==='#launch'){
      for(var key in APPS){
        if(u.indexOf(key)!==-1){
          eng.style.display='flex';
          document.documentElement.style.overflow='hidden';
          document.body.style.overflow='hidden';
          if(frm.src==='about:blank'||frm.src===''){
            ld.style.display='flex';
            if(hud)hud.style.display='none';
            frm.src=APPS[key];
            frm.onload=function(){
              setTimeout(function(){
                ld.style.display='none';
                if(hud)hud.style.display='flex';
                triggerHud();
                console.log("%c " + owner + " Engine v1.0 ", "background:#DE0000;color:#fff;font-weight:900;padding:3px;");
              },1500);
            };
          }
          return;
        }
      }
    } else {
      if(eng)eng.style.display='none';
      document.documentElement.style.overflow='auto';
      document.body.style.overflow='auto';
      if(frm)frm.src='about:blank';
      if(hud)hud.style.display='none';
    }
  }

  window.addEventListener('hashchange', runner);
  window.addEventListener('load', runner);
  runner();
})(ENGINE_AUTHOR);
